import React from 'react';
import './assets/styles/style.css'
import {AnswersList, Chats} from './components/index'
import defaultDataset from './dataset'

export default class App extends React.Component{
  constructor(props){
    super(props);
    this.state={
      chats:[],
      answers:[],
      currentId:"init",
      dataset:defaultDataset
    }
  }

  selectAnswer(nextQuestionId,selectAnswer){
    switch(true){
      case (nextQuestionId="init"):
        break;
      default:
        const chat = {
          text:selectAnswer,
          type:"answer"
        }
        const chats = this.state.chats
        chats.push(chat)
        this.setState({
          chats:chat,
          currentId:nextQuestionId
        })
      }
  }

  initChat(){
    const initialDataset = this.state.dataset[this.state.currentId]
    const initialQuestion = initialDataset.question
    const chat = {
      text:initialQuestion,
      type:"question"
    }
    const chats = this.state.chats
    chats.push(chat)
    this.setState({
      chats:chats
    })
  }

  initAnswer(){
    const initialDataset = this.state.dataset[this.state.currentId]
    const initialAnswer = initialDataset.answers
    this.setState({
      answers:initialAnswer
    })
  }

  componentDidMount(){
    this.initChat();
    this.initAnswer();
  }

  render(){
    return(
      <section className="c-section">
        <div className="c-box">
          <Chats chats={this.state.chats} />
          <AnswersList answers={this.state.answers}/>
        </div>
      </section>
    );
  }
};
