import React from 'react';
import './assets/styles/style.css'
import AnswersList from './components/index'
import defaultDataset from './dataset'

export default class App extends React.Component{
  constructor(props){
    super(props);
    this.state={
      answers:[],
      currentId:"init",
      dataset:defaultDataset
    }
  }

  initialState(){
    const initialDataset = this.state.dataset[this.state.currentId]
    const initialAnswer = initialDataset.answers
    this.setState({
      answers:initialAnswer
    })
  }

  componentDidMount(){
    this.initialState();
  }

  render(){
    return(
      <section className="c-section">
        <div className="c-box">
          <AnswersList />
        </div>
      </section>
    );
  }
};
